from tmdbv3api.tmdb import TMDb

class Provider(TMDb):
    _urls = {"movie_provider": "/watch/providers/movie", "tv_provider": "/watch/provider/tv"}

    def movie_provider(self):
        return self._get_obj(self._call(self._urls["movie_provider"], ""), key="results")

    def tv_provider(self):
        return self._get_obj(self._call(self.urls["tv_provider"], "",), key="results")